      contact = Admin::Contact.create! valid_attributes
      get :index, params: {}, session: valid_session
      expect(assigns(:admin_contacts)).to eq([contact])
    end
  end

  describe "GET #show" do
    it "assigns the requested admin_contact as @admin_contact" do
      contact = Admin::Contact.create! valid_attributes
      get :show, params: {id: contact.to_param}, session: valid_session
      expect(assigns(:admin_contact)).to eq(contact)
