      get :new, params: {}, session: valid_session
      expect(assigns(:admin_contact)).to be_a_new(Admin::Contact)
    end
  end

  describe "GET #edit" do
    it "assigns the requested admin_contact as @admin_contact" do
      contact = Admin::Contact.create! valid_attributes
      get :edit, params: {id: contact.to_param}, session: valid_session
      expect(assigns(:admin_contact)).to eq(contact)
    end
  end

  describe "POST #create" do
    context "with valid params" do
      it "creates a new Admin::Contact" do
        expect {
          post :create, params: {admin_contact: valid_attributes}, session: valid_session
        }.to change(Admin::Contact, :count).by(1)
      end

      it "assigns a newly created admin_contact as @admin_contact" do
        post :create, params: {admin_contact: valid_attributes}, session: valid_session
